mapper.select:
SELECT lpa.id_ AS id, lpi.name_ as productName, lpi.id_ as productInfoId, lpa.location_ as location, CASE lpa.location_ WHEN 0 THEN '启动图' WHEN 1 THEN '首页弹框' WHEN 2 THEN '首页轮播图' END AS typeName, lpa.client_name AS clientName, DATE_FORMAT(lpa.create_time,'%Y-%m-%d') as dataTime FROM loan_product_advertising lpa LEFT JOIN loan_product_info lpi ON lpi.id_ = lpa.product_info_id and lpi.del_flag = 0 WHERE lpa.del_flag = 0
and lpi.name_ like CONCAT('%','${advertisingReceiveDTO.productName}','%')
and lpa.client_name = #{advertisingReceiveDTO.clientName}
